import { Component, OnInit } from '@angular/core';
import { ApiMercadoLivreService } from '../services/api-mercado-livre.service';
import { Observable } from 'rxjs';
import { Item } from '../models/itens';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent implements OnInit {
  inputValue: string = '';
  itens: Item[] = [];

  constructor(public apiML: ApiMercadoLivreService) {}
  onInputChanged(event: Event): void {
    this.inputValue = (event.target as HTMLInputElement).value;
  }
  ngOnInit(): void {
    this.searchItens();
  }

  searchItens(): void {
    if (this.inputValue.trim() === '') {
      return;
    }


    this.apiML.getItens(this.inputValue).subscribe(
      (result) => {
        this.itens = result;
      },
      (error) => {
        console.log(error);
      }
    );
  }
}
<!DOCTYPE html>
<html lang="en">

<head>
  <meta charset="UTF-8">
  <meta http-equiv="X-UA-Compatible" content="IE=edge">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/css/bootstrap.min.css">
  <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.6.4/jquery.min.js"></script>
  <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/js/bootstrap.min.js"></script>
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.5.0/semantic.min.css">
  <title>Document</title>
</head>

<body>
  <nav class="navbar bgcor">
    <div class="container-fluid">
      <div class="navbar-header">

        <a class="navbar-brand" href="#"><img class="logomenu img-fluid" src="../assets/icons/icon-144x144.png" alt=""></a>
        <img class="galho img-fluid"src="../../assets/img/galho.png" alt="">

      </div>
      <ul class="nav navbar-nav">
        <li class="active">
        <form class="form-inline">
          <div class="ui icon input">
            <input class="pesquisa"type="search" [(ngModel)]="inputValue" placeholder="Search..." (input)="onInputChanged($event)">
            <button type="button" class="ui icon botaoPes" (click)="searchItens()"><i
                class="inverted circular search link icon"></i></button>
          </div>
        </form>
      </ul>

    </div>
  </nav>





</body>

</html>
