import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Itens } from '../models/itens';
import { Observable, map } from 'rxjs';

interface ItemResponse {
  results: Array<any>;
}

@Injectable({
  providedIn: 'root'
})
export class ApiMercadoLivreService {

  private API_MercadoLivre = "https://api.mercadolibre.com/sites/MLB/search?q=";

  constructor(
    private http : HttpClient ) { }


  private mapItens(itens: any[]): Itens[] {
    return itens.map((item: { id: any; title: any; price: any;}) => ({
      id: item.id,
      title: item.title,
      price: item.price
    }));
  }

  public getItens (): Observable<Itens[]> {
    return this.http.jsonp(this.API_MercadoLivre, '{UserRequest}')
    .pipe(
      map((resultado: any) => this.mapItens(resultado.results))
      //map((res: CarResponse) => this.mapMarcas(res.Makes))
    );
  }
}
